import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import Form from './styles/Form';

const CREATE_PRODUCT_MUTATION = gql`
mutation CREATE_PRODUCT_MUTATION(
   $name: String!,
   $description: String!,
   $price: integer!,
   $image: upload,
){
    createProduct(data:{
      name: "Sample Product One",
      price: 34000,
      description: "Just a sample Product";
      status: "AVAILABLE"
    })
    {
      name
      id
      description
    }
  }
  `;

export default function CreateProduct() {
  const { inputs, handleChange, clearForm, resetForm } = useForm({
    image: '',
    name: 'NWT Supergas',
    price: 5500,
    description: "Unworn white supergas. The people's show of italy",
  });
  return (
    <Form
      onSubmit={(e) => {
        e.preventDefault();
        console.log(inputs);
      }}
    >
      <fieldset>
        <label htmlFor="image">
          Product Image
          <input
            required
            type="file"
            id="image"
            name="image"
            onChange={handleChange}
          />
        </label>
        <label htmlFor="name">
          Name
          <input
            required
            type="text"
            id="name"
            name="name"
            placeholder="Product Name"
            value={inputs.name}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="Product Price"
            value={inputs.price}
            onChange={handleChange}
          />
        </label>
        <label htmlFor="description">
          Description
          <textarea
            type="text"
            id="description"
            name="description"
            placeholder="Product Description"
            value={inputs.description}
            onChange={handleChange}
          />
          <button type="submit">Add Product</button>
        </label>
      </fieldset>
    </Form>
  );
}
